BACKEND_PROTOCOL = 'http';
BACKEND_DOMAIN = 'localhost';
BACKEND_PORT = '5000';
BACKEND_BASE_ROUTE = `${BACKEND_PROTOCOL}://${BACKEND_DOMAIN}/${BACKEND_PORT}`;

function getBackendURI(){
    return BACKEND_BASE_ROUTE
}

export default {getBackendURI};
